import React, { Component } from "react";
import {
    View,
    Text,
    StyleSheet,
    SafeAreaView,
    TouchableOpacity,
    Platform,
    StatusBar,
    ImageBackground,
    Image
} from "react-native";

export default class HomeScreen extends Component {


    render() {
        return (
            <View style={styles.container}>
                <SafeAreaView style={styles.droidSafeArea} />
                {/* <Background 
                        source={require('../assets/stars.gif')} 
                        style={styles.backgroundImage}> 
                */}
                {/* <Image 
                        source={require('../assets/stars.gif')} 
                        style={styles.backgroundImage}> 
                */}
                 <ImageBackground 
                        source={require('../assets/stars.gif')} 
                        style={styles.backgroundImage}> 

                {/* <BackgroundImage
                        source={require('../assets/stars.gif')} 
                        style={styles.backgroundImage}> 
                */}
                    <View style={styles.titleBar}>
                        <Image source={require("../assets/main-icon.png")} style={{ width: 150, height: 150 }}></Image>
                        <Text style={styles.titleText}>App</Text>
                        <Text style={styles.titleText}>Estelar</Text>
                    </View>

                    {/* <TouchableOpacity 
                          style={styles.routeCard} onPress={() =>
                        this.props.navigation("SpaceCraft")
                    }> */}
                    {/* <TouchableOpacity 
                          style={styles.routeCard} onPress={() =>
                        navigation.navigate("SpaceCraft")
                    }> */}
                    {/* { <TouchableOpacity 
                          style={styles.routeCard} onPress={() =>
                        this.props("SpaceCraft")
                    }> } */}
                     <TouchableOpacity 
                          style={styles.routeCard} onPress={() =>
                        this.props.navigation.navigate("SpaceCraft")
                    }>                    
                        <Text style={styles.routeText}>Naves Espaciais</Text>
                        <Image source={require("../assets/space_crafts.png")} style={styles.routeImage}></Image>
                    </TouchableOpacity>

                    <TouchableOpacity style={styles.routeCard} onPress={() =>
                        this.props.navigation.navigate("StarMap")
                    }>
                        <Text style={styles.routeText}>Mapa Estelar</Text>
                        <Image source={require("../assets/star_map.png")} style={styles.routeImage}></Image>
                    </TouchableOpacity>

                    <TouchableOpacity style={styles.routeCard} onPress={() =>
                        this.props.navigation.navigate("DailyPic")
                    }>
                        <Text style={styles.routeText}>Fotos Di√°rias</Text>
                        <Image source={require("../assets/daily_pictures.png")} style={styles.routeImage}></Image>
                    </TouchableOpacity>
                </ImageBackground>
            </View>
        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1
    },
    droidSafeArea: {
        marginTop: Platform.OS === "android" ? StatusBar.currentHeight : 0
    },
    backgroundImage: {
        flex: 1,
        resizeMode: 'cover',
    },
    routeCard: {
        flex: 0.12,
        justifyContent: "center",
        alignItems: "center",
        margin: 10,
        marginLeft: 30,
        marginRight: 30,
        borderRadius: 100,
        backgroundColor: "white"
    },
    titleBar: {
        flex: 0.5,
        justifyContent: "center",
        alignItems: "center"
    },
    titleText: {
        fontSize: 40,
        fontWeight: "bold",
        color: "white"
    },
    routeText: {
        fontSize: 25,
        fontWeight: "bold",
        color: '#D11583',
        justifyContent: "center",
        alignItems: "center"
    },
    routeImage: {
        position: "absolute",
        top: -20,
        right: -15,
        height: 80,
        width: 80,
        resizeMode: "contain"
    }
});
